!function(){const e=document.querySelector(".logo"),r=document.querySelector(".header-nav"),c=document.querySelector(".burger-button"),o=document.querySelector(".close-menu__img");c.onclick=(c=>{r.classList.add("header-nav_open"),e.classList.add("header-wrapper_center")}),o.onclick=(c=>{r.classList.remove("header-nav_open"),e.classList.remove("header-wrapper_center")}),r.onclick=(c=>{r.classList.remove("header-nav_open"),e.classList.remove("header-wrapper_center")})}(),function(){const e=document.querySelector(".header");window.onscroll=(r=>{window.pageYOffset>50?e.classList.add("header__active"):e.classList.remove("header__active")})}();